import React, { useState, useEffect } from 'react'
import Transaction from '../components/Transaction'

const TransactionsContainer = props => {

  const [transactions, setTransactions] = useState('')

  useEffect(() => {
    if (props.userData) {
      // API request from nested endpoint user/:id/transactions
      fetch(`http://localhost:3000/users/${props.userData.id}/transactions`)
        .then(resp => resp.json())
        .then(userTransactions => {
          setTransactions(userTransactions.data)
        })
    }
  }, [props.userData])

  const renderTransactions = transactions.map(transaction => {
    return <Transaction transactionData={transaction}/>
  })

  return(
    <ul>
      {transactions ? renderTransactions : null}
    </ul>
  )
}

export default TransactionsContainer
