import React from 'react'

class Stock extends React.Component {

  state = {
    price: ''
  }
  componentDidMount() {
    fetch(`https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=${Object.keys(this.props.stockData)[0]}&apikey=GJNL5RPAWAUNFOK6`)
      .then(resp => resp.json())
      .then(stockData => {
        this.setState({ price: parseFloat(stockData["Global Quote"]["05. price"]) })
      })
      .catch(error => {
        console.log(error)
        alert(error)
      })
  }
  // componentDidUpdate(prevProps) {
  //   if (this.props.userID !== prevProps.userID) {
  //     this.setState();
  //   }
  // }

  render() {
    // debugger
    return (
      <li>
        {Object.keys(this.props.stockData)[0]} - {Object.values(this.props.stockData)[0]} Shares Value: {Math.round(this.state.price * Object.values(this.props.stockData)[0] * 100) / 100}
      </li>
    )
  }
}

export default Stock
