import React from 'react';
import { Switch, Route, Redirect } from 'react-router-dom'
import SignIn from './containers/SignIn'
import Register from './containers/Register'
import Portfolio from './containers/Portfolio'

class App extends React.Component {

  state = { userData : '' }

  handleSubmit = (userData, history, endpoint, alertMessage) => {
    fetch(`http://localhost:3000/${endpoint}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify(userData)
    })
      .then(resp => resp.json())
      .then(parsedResponse => {
        if (parsedResponse.token) {
          localStorage.setItem('token', parsedResponse.token)
          this.setState({ userData : parsedResponse.user })
          history.push('/portfolio')
        } else {
          alert(alertMessage)
        }
      })
  }

  render() {
    return (
      <Switch>
        <Route path='/portfolio' render={(routerProps) => <Portfolio {...routerProps} userData={this.userData} />} />
        {
          localStorage.token ?
            <Redirect to='/portfolio' />
            :
            <Route path='/' render={(routerProps) => <SignIn {...routerProps} handleSubmit={this.handleSubmit} />} />
        }
        {
          localStorage.token ?
            <Redirect to='/portfolio' />
            :
            <Route path='/register' render={(routerProps) => <Register {...routerProps} handleSubmit={this.handleSubmit} />} />
        }
      </Switch>
    );
  }
}


export default App;
